import { Button } from '@material-ui/core'
import { Context } from '../context/Context';

const AddPost = () => {

    const [Des, setDes] = useState("")
    const [photo, setphoto] = useState({})

    const {user} = useContext(Context)

    const handlePostUpload = async (e) =>{
        e.preventDefault() 
        const data = new FormData()
        const fname = user._id + Date.now() + photo.name 
        
        // console.log(fname)
        // console.log(data)
        // console.log(Des)
        // console.log(photo)
        

        // console.log("EEEEEEEEE")
        // console.log(e)
    }

    return (
        <>
            <form enctype="multipart/form-data" onSubmit = {(e)=>handlePostUpload(e)}>
                <div className="container">
                    <div className="box">
                        <div className="text">
                            <input type="text" onChange={(e)=> setDes(e.target.value) } placeholder="What's on your mind..." ></input>
                        </div>
                        <div className="photo">  
                            <input type="file" onChange={ (e) => setphoto(e.target.files[0]) } ></input>
                        </div>
                        <Button type="submit" >Post</Button>
                    </div>
                </div>
            </form>
        </>
    )
}

export default AddPost
