import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Avatar, Card, CardContent, CardHeader, CardMedia, Typography, Box, IconButton, CardActions, Button } from '@material-ui/core';
import './Post.css';

const Post = ({ post }) => {
    const publicFolder = "http://localhost:5000/Images/"
    const [user, setuser] = useState({})
    useEffect(() => {

        const fetchUser = async () => {
            const currUser = await axios.get(`user/${post.userId}`)
            setuser(currUser.data)
        }
        fetchUser()
    
    }, [])

    

    const handleLike = () => {

    }

    const handleSave = () => {
        
    }
    return (
        <>
            <div style={{ marginTop: "5%", width: "40vw" }}>
                <Card >
                    <CardHeader
                        avatar={
                            <Box mr={2}>
                                <Avatar src={publicFolder + user.profilepic} />
                            </Box>
                        }
                        title={user.userName}
                        subheader={user.name}
                    />
                    <CardMedia
                        component="img"
                        image={publicFolder + post.photo}
                        height="250"
                        width="400"
                    />
                    <CardContent>
                        <Box ml={4}>
                            <Typography  >
                                {post.caption}
                            </Typography>
                        </Box>
                    </CardContent>
                    <CardActions>
                        <div>
                            <button className="Post_like" onclick={handleLike}><i class="fas fa-heart fa-2x"></i></button>
                            <button className="Post_save" onclick={handleSave}><i class="fas fa-bookmark fa-2x"></i></button>
                        </div>
                    </CardActions>
                </Card>
            </div>


        </>

    );
}

export default Post;
