import React , {useState,useEffect} from 'react';
import './signup.css';
import { Link } from "react-router-dom";
import Navbar from './Navbar';
import axios from 'axios';
import {ToastContainer,toast} from 'react-toastify';
import {Button} from '@material-ui/core';

const Signup = () => {

    const [name,setName] = useState("")
    const [uname,setUname] = useState("")
    const [age,setAge] = useState(0)
    const [phone,setPhone] = useState("")
    const [email, setEmail] = useState("")
    const [pass, setPass] = useState("")
    const [Repass, setRepass] = useState("")
    const [gender,setGender] = useState("")
    const publicfolder="http://localhost:5000/Images/"
    const handleSubmit = async (e) => {
        e.preventDefault()
        try{
            const newUser = {
                userName: uname,
                contact:  phone,
                age: age,
                name: name,
                password : pass,
                email : email,
                gender : gender,
                profilepic :"AVATAR.png",
            } 
            const user = await axios.post(`auth/register`,newUser)
            window.location.replace('/')
        } catch(err){
            console.log(err.message)
        } 
    } 

    return(
        <>
        <Navbar/>
        <div className="container">
            <div className="box">

                Signup!<br/><br/><br/>
                    <form onSubmit={(e)=>handleSubmit(e)} >
                    <input type="text"  placeholder="Enter Name" required onChange={(e)=>setName(e.target.value)}  /><br/>
                    <input type="text"  placeholder="Enter Number" required onChange={(e)=>setPhone(e.target.value)}  /><br/>
                    <input type="text"  placeholder="Enter Age" required onChange={(e)=>setAge(e.target.value)} /><br/>
                    <input type="radio" id="male" value="Male" name="gender" required onChange={(e)=>setGender(e.target.value)} />Male
                    <input type="radio" id="female" value="Female" name="gender" required  onChange={(e)=>setGender(e.target.value)} />Female
                    <input type="text" placeholder="Enter Username" required onChange={(e)=>setUname(e.target.value)} /><br/>
                    <input type="email" placeholder="Enter email" required onChange={(e)=>setEmail(e.target.value)} /><br/>
                    <input type="password" placeholder="Enter Password" required onChange={(e)=>setPass(e.target.value)} />
                    <input type="password" placeholder="Confirm Password" required onChange={(e)=>setRepass(e.target.value)} />
                    <br/>    <br/>
                    <Button variant="3" color="primary" type="submit" className="signupbutton" >Signup!</Button><br /><br />
                    Already a User ? <Link to="/">Click here</Link>
                </form>
                
            </div>
        </div>
        </>
    );
} 
export default Signup;
