import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Avatar, Card, CardContent, CardHeader, CardMedia, Typography, Box , IconButton } from '@material-ui/core';


const Post = ({ post, props }) => {
    const publicFolder = "http://localhost:5000/Images/"
    const [user, setuser] = useState({})
    useEffect(() => {

        const fetchUser = async () => {
            const currUser = await axios.get(`user/${post.userId}`)
            setuser(currUser.data)
        }
        fetchUser()
    }, [])

    return (
        <>
            <div style={{ marginTop: "5%", width: "40vw" }}>
                <Card >
                    <CardHeader
                        avatar={
                            <Box mr={2}>
                                <Avatar src={publicFolder + user.profilepic} />
                            </Box>
                        }
                        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }

                        title={user.userName}
                        subheader={user.name}
                    />
                    <CardMedia
                        component="img"
                        image={publicFolder + post.photo}
                        height="250"
                        width="400"
                    />
                    <CardContent>
                        <Box ml={4}>
                            <Typography  >
                                {post.caption}
                            </Typography>
                        </Box>
                    </CardContent>
                </Card>
            </div>


        </>

    );
}

export default Post;
