import axios from 'axios';
import react, { useEffect, useState } from 'react';
import PostTop from './PostTop';
import PostMid from './PostMid';
import PostBottom from './PostBottom';
import { Avatar, Card, CardContent, CardHeader, CardMedia, Typography, Box ,IconButton} from '@material-ui/core';
import MoreVertIcon from '@material-ui/icons/MoreVert';


// const useStyles = makeStyles(theme => ({

//     Post_Caption : {
//         alignItems: 'center',
//         textAlign: 'center'
//     }
// }));

const Post = ({ post, props }) => {
    // console.log(post)
    const publicFolder = "http://localhost:5000/Images/"

    const [user, setuser] = useState({})
    useEffect(() => {

        const fetchUser = async () => {
            const currUser = await axios.get(`user/${post.userId}`)
            setuser(currUser.data)
        }
        fetchUser()
    }, [])
    // const classes  = useStyles();

    return (
        <>
            {/* <div className="post">
                <PostTop user = {user} />
                <PostMid post = {post} />
                <PostBottom post = {post} />
            </div> */}
            <div style={{ marginTop: "5%", width: "40vw" }}>
                <Card >
                    <CardHeader
                        avatar={
                            <Box mr={2}>
                                <Avatar src={publicFolder + user.profilepic} />
                            </Box>
                        }

                        title={user.userName}
                        subheader={user.name}
                        

                    />
                    <CardMedia
                        component="img"
                        image={publicFolder + post.photo}
                        height="250"
                        width="400"
                    />
                    <CardContent>
                        <Box ml={4}>
                            <Typography  >
                                {post.caption}
                            </Typography>
                        </Box>
                    </CardContent>
                </Card>
            </div>


        </>

    );
}

export default Post;
