const Reducer = (state,action) =>
{
    switch (action.type) {
        case "LOGIN":
            return {
                user : action.payload
            }
            break;
        case "LOGOUT" : 
            return {
                user : null
            }
            break;
        default:
            return state
            break;
    }
}


export default Reducer;
