import React from 'react'
import NavbarHome from './NavbarHome'
import TopProfile from './TopProfile'
import BottomProfile from './BottomProfile'
import './UserProfile.css'
import {useState,useEffect} from 'react'
import axios from 'axios'
import SinglePost from './SinglePost'
export const UserProfile = () => {
  const [profileInfo,setprofileInfo]=useState({});
  const [posts,setPosts]=useState([]);
  const currentid="6125e700dc75a24a44183340"
  let postsImages=[];
  useEffect(()=>{
    const fetchProfile = async() =>
    {
      try
      {
        const res = await axios.get('user/'+currentid)
        setprofileInfo(res.data);
       
      }
      catch(err)
      {
        console.log(err.message);
      }
    }
    fetchProfile();

  },[])

  console.log(profileInfo)
  const topProfile = {
    userName : profileInfo.userName,
    friends :  profileInfo.friends,
    postCount : profileInfo.postCount,
    photo : profileInfo.profilepic,
    friendsCount : profileInfo.friendCount
  }
  // console.log(topProfile);

  console.log(typeofprofileInfo.posts)
  // const bottomProfile = {
  //   posts:profileInfo.posts
  // }
  // console.log(bottomProfile)
  // console.log()
  return (
    <>
      <NavbarHome />
       <div className="userProfileInfo">
       <TopProfile topProfile={topProfile} />
        {/* <BottomProfile bottomProfile={profileInfo.posts} /> */}
        {profileInfo.posts.map((p)=>{
            <SinglePost post={p}/>
        })}
      </div>
    </>
  )
}
