        <>
            <NavbarHome />
            <div className="Container">

                <div className="Post">
                    <div className="LeftComponent">
                        <img src={pic} alt="" className="postImg" />

                    </div>
                    <div className="RightComponent">
                        <div>
                            <PostTop user={user} />
                            <div className="caption">
                                <span ><h4>{post.caption}</h4></span>
                            </div>
                            <hr />
                            {
                                comment.map((e) => (
                                    <Comment comment={e} />
                                ))
                            }
                        </div>
