import React , {useState,useEffect} from 'react';
import './signup.css';
import { Link } from "react-router-dom";
import Navbar from './Navbar';
import axios from 'axios';


const Submit = () => {




    window.location.replace('/') 
    // window.location.replace('http://localhost:3000/')
}





const Signup = () => {

    const [name,setName] = useState("")
    const [uname,setUname] = useState("")
    const [age,setAge] = useState(0)
    const [phone,setPhone] = useState("")
    const [email, setEmail] = useState(initialState)

    const {user,dispatch} = useContext(Context)
    const currId = user._id;
    useEffect(() => {
        const fetchInfo = async () => {
            try {
                const uInfo = await axios.get(`/user/${currId}`)
                setuserInfo(uInfo.data)
                setName(uInfo.data.name)
                setUname(uInfo.data.userName)
                setAge(uInfo.data.age)
                setPhone(uInfo.data.contact)
            } catch (err) {
                console.log("Error")

            }
        }
        fetchInfo()
    }, [])

    const handleUpdate = async (e) => {
        e.preventDefault()
        try{
            const updatedUser = {
                userName: uname,
                contact:  phone,
                age: age,
                name: name
            }
            console.log(updatedUser)
            await axios.put(`/user/${currId}`,updatedUser)
        }catch(err){

        }
    }





    return(
        <>
        <Navbar/>
        <div className="container">
            <div className="box">
                Signup!<br/><br/><br/>
                <input type="text" placeholder="Enter Name" onChange={(e)=>setName(e.target.value)} /><br/>
                <input type="text" placeholder="Enter Number" onChange={(e)=>setPhone(e.target.value)}/><br/>
                <input type="text" placeholder="Enter Age" onChange={(e)=>setAge(e.target.value)}/><br/>
                <input type="radio" id ="Male" name="Gender"/>
                <label for="Male">Male</label>
                <input type="radio" id ="Female" name="Gender"/>
                <label for="Female">Female</label><br/>
                <input type="text" placeholder="Enter Username" onChange={(e)=>setUname(e.target.value)}/><br/>
                <input type="email" placeholder="Enter email" onChange={(e)=>setEmail(e.target.value)}/><br/>
                <input type="password" placeholder="Enter Password" onChange={(e)=>setPass(e.target.value)}/>
                <input type="password" placeholder="Confirm Password" onChange={(e)=>setRepass(e.target.value)}/>
                <br/>    <br/>
                <button className="signupbutton" onClick={Submit} >Signup!</button><br /><br />
                Already a User ? <Link to="/">Click here</Link>
            </div>
        </div>
        </>
    );
}

export default Signup;
