import axios from 'axios';
import react, { useEffect, useState } from 'react';
import PostTop from './PostTop';
import PostMid from './PostMid';
import PostBottom from './PostBottom';
import { Avatar, Card, CardContent, CardHeader, CardMedia, Typography } from '@material-ui/core';

const Post = ({ post }) => {
    // console.log(post)
    const publicFolder = "http://localhost:5000/Images/"

    const [user, setuser] = useState({})
    useEffect(() => {

        const fetchUser = async () => {
            const currUser = await axios.get(`user/${post.userId}`)
            setuser(currUser.data)
        }
        fetchUser()
    }, [])

    const styles = {
        
        Post_Caption : {
            alignItems: 'center',
            textA
        }
    }
    return (
        <>
            {/* <div className="post">
                <PostTop user = {user} />
                <PostMid post = {post} />
                <PostBottom post = {post} />
            </div> */}
            <div style={{ marginTop: "5%" }}>
                <Card >
                    <CardHeader
                        avatar={
                            <Avatar src={publicFolder + user.profilepic} />
                        }
                        title={user.userName}
                        subheader={user.name}
                    />
                    <CardMedia
                        component="img"
                        image={publicFolder + post.photo}
                        height="250"
                        width="400"
                    />
                    <CardContent>
                        <Typography align-items="center" className="Post_Caption">
                            {post.caption}
                        </Typography>
                    </CardContent>
                </Card>
            </div>


        </>

    );
}

export default Post;
